mapper.select:
SELECT 

FROM hyk_oil_manager a

WHERE a.id = #{id}

SELECT 

FROM hyk_oil_manager a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.order_no = #{orderNo}


		AND a.user_id = #{hykUser.id}


		AND a.periods_num = #{periodsNum}


		AND a.oil_date = #{oilDate}


		AND a.plan_oil_date = #{planOilDate}


		AND a.status = #{status}


		AND a.remark = #{remark}


		AND a.goodId = #{goodId}


		AND a.orderId = #{orderId}


		AND a.cardId = #{cardId}


		AND DATE_FORMAT(a.plan_oil_date,'%Y%m%d')>=DATE_FORMAT(#{startTime},'%Y%m%d')


		AND DATE_FORMAT(a.plan_oil_date,'%Y%m%d')<=DATE_FORMAT(#{endTime},'%Y%m%d')


		AND (a.order_no like CONCAT('%',#{other},'%') or u3.phone like CONCAT('%',#{other},'%') or a.user_id like CONCAT('%',#{other},'%'))


		AND b.order_type =#{hykOrder.orderType}


ORDER BY a.`status`,a.plan_oil_date

SELECT 

FROM hyk_oil_manager a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE hyk_oil_manager SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT date_format(a.plan_oil_date,'%Y') as year,date_format(a.plan_oil_date,'%m') as month,date_format(a.plan_oil_date,'%d') as day , date_format(a.plan_oil_date,'%Y%m%d') as registerDateStr, a.status as status ,a.money as money ,c.`name` as cardName , insert( c.oli_card_no,5,case LENGTH(c.oli_card_no) when 16 then 8 when 19 then 11 else 1 end, case LENGTH(c.oli_card_no) when 16 then '********' when 19 then '***********' else '*' end ) as cardNum , a.periods_num as periodsNum,b.cycle as cycle,b.goods_type as goodsType from hyk_oil_manager a left join hyk_goods b on a.goodId=b.id left join hyk_oil_card c on a.cardId=c.id
a.user_id=#{userId} and FROM_UNIXTIME(UNIX_TIMESTAMP(a.plan_oil_date),'%Y%m') =#{time} and a.`status`=#{status} and a.del_flag=0
AND FROM_UNIXTIME(UNIX_TIMESTAMP(a.plan_oil_date),'%d') =#{day}
